import React from 'react'
import { useState } from 'react'
import styled from 'styled-components';

export const Carrito = () => {

    const [carrito, cambiarCarrito] = useState([
        {id:1, cantidad:2, nombre: 'Producto1'},
        {id:2, cantidad:1, nombre: 'Producto2'},
        {id:3, cantidad:5, nombre: 'Producto3'},
        {id:4, cantidad:7, nombre: 'Producto4'},
        {id:5, cantidad:9, nombre: 'Producto5'},
        {id:6, cantidad:11, nombre: 'Producto6'},
    ]);

    return (
        <div>
            <h3>Carrito de Compras</h3>
            {carrito.length > 0 ?
                carrito.map((producto, index)=>{
                    return (
                        <Producto>
                            <NombreProducto>
                                {producto.nombre}
                            </NombreProducto>
                        </Producto>
                    )
                })
            :
                <p>No has agregado productos al carrito</p>
            }
        </div>
    )
}

const Producto = styled.div`
    padding: 10px;
    border-bottom: 1px solid #ebebf3;
    font-size: 14px;
`;

const NombreProducto = styled.p`
    font-weight: bold;
    font-size: 16px;
    color: #000;
`
